<?php//定义一个ajax请求需要的信息（json格式）	define("DSN","mysql:host=localhost;dbname=ff");	define("USER","root");	define("PASS","123456");	try{		//获取连接		$pdo = new PDO(DSN,USER,PASS);		//通过setAttribute方法来设置PDO中的属性信息（报错模式(异常模式)）		$pdo->setAttribute(PDO::ATTR_ERRMODE,PDO::ERRMODE_EXCEPTION);		$pdo->query("set names utf8");//设置编码	}catch(PDOException $pe){		die('数据库连接失败！原因:'.$pe->getMessage());	}	try{		//执行查询,并获取结果处理对象PDOStatment		$sql = "select id,name from ff_goods_type where pid={$_GET['id']}";		$stmt = $pdo->query($sql);		$result=array();		//遍历输出		while($ob = $stmt->fetch(PDO::FETCH_OBJ)){			$result[]=$ob;		}			}catch(PDOException $pe){		echo "执行失败！原因：".$pe->getMessage();			}	echo json_encode($result);